import{_ as p,c as e}from"./app.1374bc43.js";const a={},o=e('<h1 id="_2020-06-19" tabindex="-1"><a class="header-anchor" href="#_2020-06-19" aria-hidden="true">#</a> 2020-06-19</h1><p>\u4ECA\u59297\u70B9\u591A\u8D77\u5E8A\u7684\uFF0C\u5FC3\u91CC\u4E00\u76F4\u60F3\u7740\u660E\u592920\u53F7\u53EF\u4EE5\u6709\u65B0\u7684\u82B1\u68D2\u53EF\u4EE5\u79CD\uFF0C\u6240\u4EE5\u4E00\u76F4\u671F\u76FC\u8FD9\u8D76\u5FEB\u62FF\u523021320\u514B,\u524D\u51E0\u5929\u4E3A\u4E86\u6F02\u4EAE\u7684\u88C5\u9970\u54C1\uFF0C\u82B1\u6389\u4E86\u6574\u6574900\u514B,\u5929\u6740\u7684\uFF0C\u73B0\u5728\u771F\u7684\u662F\u592A\u9700\u8981\u90A3900\u514B\u4E86\uFF0C\u4EE5\u540E\u6709\u8FD9\u79CD\u88C5\u626E\uFF0C\u575A\u51B3\u62B5\u5236\u3002\u6211\u8981\u7684\u662F\u79CD\u6811\uFF0C\u4E0D\u662F\u770B\u8D77\u6765\u82B1\u91CC\u80E1\u54E8\u7684\u88C5\u626E\u3002</p><p>\u6574\u4E2A\u4E0A\u5348\u90FD\u662F\u5728\u624B\u673A\u5C4F\u5E55\u524D\u5EA6\u8FC7\u7684\uFF0C\u53EF\u4EE5\u53BB\u6B7B\u4E00\u4E07\u6B21\u4E86\uFF0C\u79BD\u517D\u4E0D\u5982\u3002\u6628\u5929\u8BA4\u8BC6\u5230\uFF1A\u5BF9\u4E8E\u624B\u673A\u5206\u4E24\u79CD\u60C5\u51B5\uFF0C\u4E00\u3001\u4F60\u5728\u9700\u8981\u7684\u65F6\u95F4\u4F7F\u7528\u5B83\uFF0C\u5B83\u5BF9\u4F60\u6765\u8BF4\uFF0C\u662F\u4E2A\u597D\u5E2E\u624B\u3002\u4E8C\u3001\u5B83\u65F6\u65F6\u523B\u523B\u7684\u5438\u5F15\u4F60\u7684\u6CE8\u610F\u529B\uFF0C\u4F60\u88AB\u8FF7\u60D1\u3002\u6211\u4E0A\u5348\u7684\u72B6\u6001\u5B8C\u5168\u5C31\u662F\u88AB\u63A7\u5236\u4E86\uFF0C\u6211\u4E0D\u6562\u5237\u6296\u97F3\uFF0C\u4E0D\u6562\u4E0A\u5FAE\u535A\uFF0C\u4E0D\u6562\u5F00\u670B\u53CB\u5708\uFF0C\u4E0D\u6562\u770B\u89C6\u9891\uFF0C\u4E0D\u6562\u53BBB\u7AD9\uFF0C\u4E3B\u8981\u539F\u56E0\u662F\u6211\u6CA1\u6709\u5DE5\u4F5C\uFF0C\u4E0D\u662F\u5728\u529E\u516C\u5BA4\u91CC\uFF0C\u6CA1\u6709\u4EBA\u8981\u6C42\u6211\uFF0C\u6240\u4EE5\u6211\u53EF\u4EE5\u7231\u5E72\u561B\u5E72\u561B\u3002\u6211\u60F3\u6709\u4E86\u5DE5\u4F5C\uFF0C\u5C31\u4E0D\u4F1A\u6709\u90A3\u4E48\u591A\u65F6\u95F4\u73A9\u624B\u673A\u4E86\u3002</p><p>\u73CD\u7231\u751F\u547D\uFF0C\u8FDC\u79BB\u624B\u673A\u3002\u8FD9\u6BD4\u4EFB\u4F55\u9E26\u7247\u90FD\u8981\u81F4\u547D\uFF0C\u56E0\u4E3A\u5B83\u88AB\u516C\u4F17\u8BA4\u53EF\uFF0C\u5C31\u76F8\u5F53\u4E8E\u662F\u5927\u5BB6\u90FD\u53E3\u53E3\u76F8\u4F20\uFF0C\u53E3\u7891\u7206\u68DA\u7684\u65B0\u5F0F\u6BD2\u54C1\u3002</p><p>\u4E2D\u5348\u5403\u996D\uFF0C\u53BB\u8BFB\u4E66\u4E86\uFF0C\u771F\u7684\u4E0D\u6562\uFF08\u6CA1\u8138\uFF0C\u4E5F\u5ACC\u5F03\uFF09\u518D\u770B\u624B\u673A\u4E86\u3002\u624B\u673A\u662F\u4E2A\u6B7B\u7269\uFF0C\u5B83\u4E0D\u4F1A\u4E3B\u52A8\u627E\u4F60\uFF0C\u6240\u4EE5\u6211\u63A7\u5236\u4F4F\u81EA\u5DF1\u5C31\u5BF9\u4E86\u3002</p><p>\u660E\u5929\u53BB\u4EC0\u4E48\u5BB6\u88C5\u8282\uFF0C\u771F\u662F\u4ED6\u5988\u7684\u540E\u6094\uFF0C\u53BB\u54EA\u91CC\u5E72\u6BDB\uFF0C\u8FD8\u4ED6\u5988\u8981\u82B1100\u5757\u94B1\u3002\u73B0\u5728\u662F10\u5757\u94B1\u5BF9\u6211\u6765\u8BF4\uFF0C\u90FD\u662F\u5927\u6570\u76EE\u3002\u6211\u80AF\u5B9A\u6CA1\u94B1\u4E70\u4EFB\u4F55\u4E1C\u897F\u3002\u660E\u5929\u62FF\u5C0F\u672C\u672C\u53BB\u770B\u4EF7\u683C\u5427</p><p>\u6700\u540E\u7684B\u7AD9\u89C6\u9891\u8BB0\u5F97\u662F\uFF0C\u5982\u679C\u4F60\u4ED8\u51FA\u4E86\u81EA\u5DF1\u7684\u5168\u90E8\u7CBE\u529B\uFF0C\u4F1A\u600E\u4E48\u6837\uFF1F\u8FD9\u53E5\u8BDD\u771F\u7684\u5E94\u8BE5\u6BCF\u5929\u53BB\u601D\u8003\u3002\u6211\u8BA4\u4E3A\u6211\uFF08\u5C31\u4E0D\u8BF4\u6211\u8BA4\u4E3A\u5F88\u591A\u4EBA\u4E86\uFF09\uFF0C\u5C31\u662F\u4E4B\u524D\u7684\u89C6\u9891\u91CC\u7684\u90A3\u4E9B\u6D41\u6D6A\u6C49\uFF0C\u4E00\u4E2A\u5916\u56FD\u670B\u53CB\uFF0C\u62FF\u4E09\u79CD\u4E1C\u897F\u8BA9\u6D41\u6D6A\u6C49\u53BB\u9009\uFF0C1\u3001\u9EA6\u5F53\u52B3\u5DE8\u65E0\u9738\uFF0C2\u3001\u9999\u70DF\uFF0C3\u3001\u4E00\u74F6\u6717\u59C6\u9152\u3002</p><p>\u4E09\u4E2A\u6D41\u6D6A\u6C49\u90FD\u9009\u62E9\u4E86\u6717\u59C6\u9152\uFF0C\u4ED6\u4EEC\u771F\u7684\u662F\u9700\u8981\u7CBE\u795E\u4E16\u754C\u7684\u6EE1\u8DB3\u3002\u4ED6\u4EEC\u4E5F\u8BB8\u6709\u653F\u5E9C\u7684\u6551\u6D4E\u91D1\uFF0C\u6BCF\u6708\u5927\u6982600\u7F8E\u5143\uFF0C\u6709\u4E86\u8FD9\u4E9B\u94B1\uFF0C\u5C31\u4E0D\u4F1A\u997F\u6B7B\u4E86\uFF0C\u5373\u4F7F\u4EC0\u4E48\u90FD\u4E0D\u505A\uFF0C\u4E5F\u4E0D\u4F1A\u997F\u6B7B\u3002\u5982\u679C\u53BB\u5DE5\u4F5C\u4E86\uFF0C\u6551\u6D4E\u91D1\u5C31\u4E0D\u80FD\u9886\u4E86\u3002\u8FD9\u4E5F\u662F\u798F\u5229\u597D\u7684\u56FD\u5BB6\u7684\u4E00\u4E2A\u95EE\u9898\u5427\uFF0C\u56E0\u4E3A\u4E5F\u8BB8\u53EA\u6709\u5728\u4E2D\u56FD\uFF0C\u4EBA\u4EEC\u4F1A\u8BA4\u4E3A\uFF0C\u53EA\u8981\u4F60\u80AF\u4ED8\u51FA\uFF0C\u5C31\u4F1A\u6709\u5DE5\u4F5C\u505A\uFF0C\u5C31\u4F1A\u6323\u5230\u94B1\uFF0C\u5373\u4F7F\u56FD\u5BB6\u6CA1\u6709\u6551\u6D4E\u91D1\u7ED9\u4F60\u53D1\uFF0C\u4F60\u4E5F\u4E0D\u4F1A\u997F\u6B7B\u3002</p><p>\u8FD9\u4E48\u8BF4\u5C31\u8981\u770B\u770B\u7F8E\u56FD\u4EBA\u7684\u5C31\u4E1A\u95EE\u9898\u4E86\uFF0C\u53EF\u80FD\u662F\u4ED6\u4EEC\u771F\u7684\u65E0\u6CD5\u5C31\u4E1A\uFF0C\u56E0\u4E3A\u5C31\u4E1A\u9700\u8981\u4E00\u4E2A\u56FA\u5B9A\u7684\u751F\u6D3B\u5730\u5740\uFF0C\u90A3\u5C31\u9700\u8981\u5148\u79DF\u623F\u5B50\uFF0C\u79DF\u623F\u5B50\u7684\u94B1\u6CA1\u6709\uFF0C\u5C31\u6CA1\u6709\u529E\u6CD5\u5C31\u4E1A\u3002\u5C31\u662F\u8BF4\u4F60\u8981\u5148\u6709\u4E00\u90E8\u5206\u94B1\uFF0C\u624D\u80FD\u6709\u5DE5\u4F5C\uFF0C\u800C\u4E0D\u662F\u5148\u6709\u5DE5\u4F5C\uFF0C\u7136\u540E\u53EF\u4EE5\u50A8\u5B58\u4E00\u90E8\u5206\u94B1\u3002</p><p>\u6211\u4E3A\u4EC0\u4E48\u4F1A\u53BB\u5173\u5FC3\u8FD9\u4E2A\u4E9B\uFF0C\u56E0\u4E3A\u6211\u8BA4\u4E3A\uFF0C\u7F8E\u56FD\u3001\u65E5\u672C\u3001\u65B0\u52A0\u5761\u3001\u745E\u58EB\u3001\u4E39\u9EA6\uFF0C\u8FD9\u4E9B\u53D1\u8FBE\u56FD\u5BB6\uFF0C\u5B83\u4EEC\u53EA\u4E0D\u8FC7\u662F\u63D0\u524D\u5230\u4E86\u90A3\u4E2A\u5730\u6B65\uFF0C\u6211\u4EEC\u4E2D\u56FD\u662F\u53D1\u5C55\u4E2D\u56FD\u5BB6\uFF0C\u6211\u4EEC\u53EF\u80FD\u4F1A\u8FDF\u65E9\u5230\u54EA\u4E00\u6B65\u3002\u4E5F\u53EF\u80FD\u6211\u5230\u4E8660\u5C81\uFF0C\u4F1A\u4E0D\u5E78\u7684\u5931\u4E1A\u3002\u7136\u540E\u5C31\u53D8\u6210\u4E86\u4E00\u4E2A\u6CA1\u529E\u6CD5\u627E\u5230\u5DE5\u4F5C\u7684\u4EBA\u3002\u6211\u53EF\u4E0D\u60F3\u8FD9\u6837\u7684\u4E8B\u60C5\u53D1\u751F\uFF0C\u6240\u4EE5\u6211\u8FD8\u662F\u60F3\u53BB\u4E86\u89E3\u4E86\u89E3\u3002</p><p>\u4E00\u4E2A\u4EBA\u7684\u5FC3\u7406\u5065\u5EB7\u662F\u5F88\u91CD\u8981\u7684\uFF0C\u5C31\u50CF\u4E00\u4E2A\u5E72\u52B2\u5341\u8DB3\uFF0C\u5145\u6EE1\u6B63\u80FD\u91CF\u7684\u4EBA\uFF0C\u548C\u4E00\u4E2A\u6CA1\u6709\u4E50\u8DA3\uFF0C\u6CA1\u6709\u8DA3\u5473\uFF0C\u505A\u4E8B\u4E22\u4E09\u843D\u56DB\u7684\u4EBA\u7684\u533A\u522B\u4E00\u6837\u5927\u3002</p><p>\u5982\u679C\u662F\u6211\uFF0C\u6211\u5F88\u60F3\u8981\u90A3\u5305\u70DF\uFF0C\u56E0\u4E3A\u6211\u4ECA\u5929\u8FD8\u6CA1\u6709\u62BD\u4E0A\u4E00\u53E3\uFF0C\u70DF\u763E\u6709\u70B9\u5927\u3002\u6BD5\u7ADF\u6211\u62BD\u4E86\u597D\u51E0\u4E2A\u6708\u65F6\u95F4\u7684\u70DF\u4E86\u3002\u4F46\u662F\u6211\u4E0D\u4F1A\u9009\u62E9\u70DF\uFF0C\u56E0\u4E3A\u60F3\u6211\u6539\u53D8\u81EA\u5DF1\uFF0C\u4ECE\u6628\u5929\u624D\u521A\u521A\u5F00\u59CB\u3002\u8FD8\u4E0D\u81F3\u4E8E\uFF0C\u4ECA\u5929\u5C31\u575A\u6301\u4E0D\u4E0B\u6765\u3002\u5982\u679C\u6211\u6709\u4E86\u6C49\u5821\uFF0C\u90A3\u5C31\u7ED9\u522B\u7684\u6D41\u6D6A\u6C49\u5427\u3002\u5E0C\u671B\u4ED6\u4EEC\u522B\u5438\u6BD2\u3001\u522B\u5815\u843D\u5728\u81EA\u5DF1\u7684\u7CBE\u795E\u4E16\u754C\u91CC\u3002\u4E0B\u9762\u662F\u89C6\u9891\u91CC\u7684\u4E00\u53E5\u8BDD\uFF1AYour actions become your habits,your habits become your values,your values become your destiny.--Mahatma Gandhi</p><p>\u884C\u52A8\u51B3\u5B9A\u4E60\u60EF\uFF0C\u4E60\u60EF\u51B3\u5B9A\u4EF7\u503C\uFF0C\u4EF7\u503C\u51B3\u5B9A\u547D\u8FD0\u3002--\u83AB\u7F55\u8FBE\u65AF\xB7\u7518\u5730</p><p>\u6211\u4E5F\u4E0D\u80FD\u5815\u843D\u5728\u81EA\u5DF1\u7684\u7CBE\u795E\u4E16\u754C\u91CC\u9762\u3002</p><p>\u96BE\u9053web\u524D\u7AEF\u771F\u7684\u6210\u4E86\u6211\u7684\u4E03\u5E74\u4E4B\u75D2\u7684\u59BB\u5B50\u4E86\u5417\uFF0C\u4E3A\u4EC0\u4E48\u4E0D\u7BA1\u4EC0\u4E48\u6837\u7684\u4E00\u70B9\u5C0F\u4E8B\uFF0C\u90FD\u80FD\u628A\u6CE8\u610F\u529B\u4ECE\u5979\u8EAB\u4E0A\u79FB\u5F00\u3002\u4E5F\u4E0D\u4F1A\u53BB\u771F\u6B63\u7684\u60F3\u53BB\u5B66\u4E60\u548C\u627E\u5DE5\u4F5C\u3002</p><p>\u524D\u7AEF\u524D\u7AEF\uFF0C\u5979\u4E0D\u53EF\u7231\u5417\uFF1F\u6211\u8F9C\u8D1F\u4E86\u4F60\uFF0C\u4F60\u4E5F\u7EC8\u5C06\u4E0D\u4F1A\u5E2E\u52A9\u6211\uFF0C\u9664\u975E\u6211\u518D\u6B21\u53BB\u8FFD\u6C42\u4F60\uFF0C\u4F60\u5C31\u7AD9\u5728\u90A3\uFF0C\u7B49\u7740\u6211\uFF0C\u7B49\u7740\u8FD9\u4E2A\u8D1F\u5FC3\u6C49\u56DE\u5BB6\u3002\u5982\u679C\u6211\u771F\u7684\u559C\u6B22\u524D\u7AEF\u8FD9\u4E2A\u5DE5\u4F5C\uFF0C\u6211\u4F1A\u8FD9\u4E48\u4E45\u65F6\u95F4\u4E0D\u4E0A\u73ED\u5417\uFF0C\u5DE5\u4F5C\u4E0D\u5C31\u662F\u7528\u6765\u6323\u94B1\u7684\u5417\uFF1F\u8FD9\u6837\u6CA1\u6709\u4EC0\u4E48\u4E0D\u5BF9\u3002\u8FD8\u662F\u56E0\u4E3A\u94B1\u7684\u95EE\u9898\uFF0C\u59A5\u534F\u4E86\u81EA\u5DF1\u3002\u518D\u8BF4\u81EA\u5DF1\u4E5F\u6CA1\u6709\u4EC0\u4E48\u597D\u9879\u76EE\u554A\u3002</p><p>\u60F3\u8981\u6539\u53D8\u547D\u8FD0\uFF0C\u5C31\u53BB\u6539\u53D8\u4EF7\u503C\uFF0C\u60F3\u53BB\u6539\u53D8\u4EF7\u503C\u5C31\u53BB\u6539\u53D8\u4E60\u60EF\uFF0C\u60F3\u53BB\u6539\u53D8\u4E60\u60EF\uFF0C\u5C31\u53BB\u51B3\u5B9A\u4EC0\u4E48\u8BE5\u505A\uFF0C\u4EC0\u4E48\u4E0D\u8BE5\u505A\u3002\u6211\u592A\u96BE\u4E86\u3002</p><p>\u6211\u4E0D\u77E5\u9053\u81EA\u5DF1\u8BE5\u4E0D\u8BE5\u8003\u91CD\u70B9\u4E2D\u5B66\uFF0C\u800C\u6CA1\u8003\u91CD\u70B9\u4E2D\u5B66\uFF0C\u4E0D\u77E5\u9053\u5927\u5B66\u5BF9\u4E8E\u4E13\u79D1\u751F\u6765\u8BF4\u662F\u4E0D\u503C\u5F97\u7684\uFF0C\u5BF9\u4E8E\u80FD\u4E0A\u672C\u79D1\u5C31\u4E0A\u672C\u79D1\u4E3A\u5565\u4E0D\u53BB\u5462\u3002\u4F60\u6709\u70B9\u667A\u6167\u884C\u4E0D\u884C\uFF1F\u4E09\u601D\u800C\u540E\u884C\u884C\u4E0D\u884C\uFF0C\u64B8\u50BB\u4E86\u5417\uFF1F\u770B\u770B\u73B0\u5728\u7684\u5E9F\u67F4\u6837\uFF0C\u771F\u662F\u8BA9\u4EBA\u9119\u89C6\u3002\u771F\u4E22\u8138\u3002\u597D\u597D\u7684\u4E00\u628A\u724C\uFF0C\u771F\u662F\u6253\u7684\u7A00\u70C2\u3002\u8BA9\u4EBA\u53EF\u601C\u8FD9\u4EF6\u4E8B\u4E0A\uFF0C\u4E5F\u5C31\u81EA\u5DF1\u628A\u81EA\u5DF1\u7ED9\u9A97\u5230\u4E86\u3002</p><p>\u65E0\u6CD5\u4E0D\u627F\u8BA4\uFF0C\u4F60\u5BF9\u4E8E\u8FD9\u4E2A\u4E16\u754C\u7684\u89C4\u5219\uFF0C\u771F\u7684\u662F\u4E00\u70B9\u4E0D\u77E5\u3002\u4E0D\u77E5\u662F\u7236\u6BCD\u7684\u9057\u4F20\u6216\u662F\u6559\u5BFC\uFF0C\u73B0\u5728\u4E0D\u4E0D\u80FD\u518D\u628A\u95EE\u9898\u63A8\u5411\u522B\u4EBA\u300227\u5C81\uFF0C\u65E9\u5C31\u8BE5\u6210\u5E74\u7684\u4F60\uFF0C\u63A5\u4E0B\u6765\u8981\u72EC\u81EA\u4E0A\u573A\u4E86\u3002\u8FD8\u6709\u5C31\u662F\uFF0C\u7559\u7ED9\u4F60\u4E0A\u5347\u7684\u65F6\u95F4\u4E0D\u591A\u4E86\uFF0C\u4F60\u7701\u7740\u70B9\u7528\u5427\u3002</p><p>\u89C2\u68CB\u4E0D\u8BED\u771F\u541B\u5B50\uFF0C\u843D\u5B50\u65E0\u6094\u5927\u4E08\u592B\u3002</p><p>\u61E6\u5F31\u65E0\u521A\u6BC5\u4E43\u7537\u4EBA\u6700\u5927\u4E4B\u803B\u3002</p><p>\u5148\u505A\u4E2A\u4EBA\uFF0C\u518D\u505A\u4E2A\u7537\u4EBA\u3001\u541B\u5B50\u3001\u5927\u4E08\u592B\u3002</p><p>\u5230\u73B0\u5728\u4E86\uFF0C\u8FD8\u662F\u4E0D\u53BB\u5B66\u4E60\uFF0C\u5FC3\u91CC\u5F88\u6D6E\u8E81\uFF0C\u7279\u96BE\u53D7\uFF0C\u600E\u4E48\u529E\uFF1F</p><p>\u4E0B\u5468\u5C31\u662F\u7AEF\u5348\u8282\u4E86\uFF0C\u53C8\u8981\u653E\u5047\u4E86\u4F46\u662F\u548C\u6211\u6CA1\u6709\u5173\u7CFB\u554A\uFF0C\u5F88\u70E6\u8E81\u3002\u6211\u90FD\u4E0D\u77E5\u9053\u81EA\u5DF1\u8981\u5E72\u4EC0\u4E48\uFF0C\u4E00\u4F1A\u505A\u70B9\u8FD9\u4E2A\u4E00\u4F1A\u505A\u70B9\u90A3\u4E2A\uFF0C\u5929\u6740\u7684\u3002\u8FD9\u770B\u6765\u771F\u662F\u75C5\u554A\u3002\u5C31\u662F\u4ED6\u5988\u7684\u4E0D\u613F\u610F\u5B66\u4E60\u3002\u771F\u662F\u4ED6\u5988\u7684\u70E6\u6B7B\u4E86\u3002\u5FC3\u60C5\u5F88\u4E0D\u597D\u3002\u4EC0\u4E48\u90FD\u4E0D\u613F\u610F\u505A\u3002</p><p>\u5C31\u662F\u6291\u90C1\u75C7\u7684\u4E09\u65E0\u8868\u73B0\uFF0C\u65E0\u7528\u3001\u65E0\u52A9\u3001\u65E0\u671B</p><p>\u4F60\u8FD8\u597D\u7684\uFF0C\u6D4B\u8BD5\u7ED3\u679C\u662F\u53EF\u80FD\u5B58\u5728\u8F7B\u5EA6\u6291\u90C1\u3002\u4E0D\u5DE5\u4F5C\u8FD9\u4E48\u4E45\u8BA9\u8C01\u6765\u6D4B\u8BD5\uFF0C\u73B0\u5728\u80AF\u5B9A\u4E5F\u90FD\u662F\u6291\u90C1\u3002</p><p>\u7238\u7238\u624D\u771F\u662F\u5F97\u4E86\u6291\u90C1\u75C7\u3002\u600E\u4E48\u8BA9\u7238\u7238\u597D\u8D77\u6765\u554A\uFF01</p><p>\u60C5\u6B4C\u91CC\u7684\u6545\u4E8B\u5C31\u662F\u5206\u624B\uFF0C\u5C31\u662F\u4E0D\u4F1A\u5728\u4E00\u8D77\u3002\u6CA1\u6709\u6700\u540E\u7684\u751C\u7F8E\uFF0C\u7559\u4E0B\u7684\u662F\u9057\u61BE\u3002</p><p>\u6211\u6709\u6545\u4E8B\uFF0C\u4F60\u6709\u9152\u5417\uFF1F</p><p>\u6709\u6545\u4E8B\u7684\u4EBA\u5450\uFF01\u4F60\u771F\u5E78\u8FD0\uFF01</p><hr><p>\u664F\u8D77\uFF0C\u6211\u73B0\u5728\u6267\u7B14\u7684\u65F6\u95F4\u662F6\u670820\u65E5\u7684\u65E9\u4E0A\uFF0C\u5E94\u8BE5\u628A19\u65E5\u7684\u4E8B\u60C5\u5199\u572819\u65E5\u7684\u65E5\u8BB0\u4E2D\u624D\u5BF9\u3002</p><p>\u6628\u5929\u4E0B\u5348\u6211\u51C6\u5907\u5B66\u4E60\uFF0C\u6700\u540E\u8FD8\u662F\u6CA1\u6709\u884C\u52A8\u3002</p><p>\u6D4B\u8BD5\u8FC7\u81EA\u5DF1\u7684\u6291\u90C1\u7A0B\u5EA6\uFF0C\u4E24\u6B21\u7ED3\u679C\u5206\u522B\u662F\u201C\u53EF\u80FD\u6709\u6291\u90C1\u201D\u548C\u201C\u8F7B\u5EA6\u6291\u90C1\u201D\u3002\u6211\u6E05\u695A\u7684\u8BB0\u5F97\uFF0C\u6211\u5728\u9009\u62E9\u5BF9\u4E8E\u672A\u6765\u662F\u5426\u62B1\u6709\u5E0C\u671B\u65F6\uFF0C\u6211\u603B\u662F\u8BA4\u4E3A\u672A\u6765\u662F\u5145\u6EE1\u5E0C\u671B\u7684\u3002\u6240\u4EE5\u6211\u7684\u5F97\u5206\u5E76\u4E0D\u662F\u53EF\u6015\u7684\u3002</p><p>\u662F\u600E\u4E48\u60F3\u5230\u60F3\u8981\u770B\u300A\u8096\u7533\u514B\u7684\u6551\u8D4E\u300B\u7684\uFF0C\u4F46\u662F\u6211\u73B0\u5728\u5FD8\u8BB0\u3002\u6211\u77E5\u9053\u81EA\u5DF1\u7535\u8111\u91CC\u6709\u5728\u4E4B\u524D\u4E0B\u8F7D\u597D\u7684\u76D7\u7248\u5F71\u7247\u3002\u6240\u4EE5\u6211\u5C31\u770B\u8D77\u6765\u4E86\u3002\u4EE5\u81F3\u4E8E\u6211\u6628\u5929\u665A\u4E0A\u5230\u4E86\u4E00\u70B9\u534A\u624D\u7761\u89C9\u3002\u54E6\uFF0C\u6211\u5E94\u8BE5\u5728\u5F00\u5934\u5199\u4E0A\u201C\u664F\u8D77\u201D\uFF08\u66FE\u5927\u5E05\u7684\u65E5\u8BB0\u4E2D\u7ECF\u5E38\u4F7F\u7528\u7684\u4E00\u4E2A\u8BCD\uFF0C\u610F\u601D\u548C\u201C\u65E9\u8D77\u201D\u5BF9\u7ACB\uFF0C\u770B\u8D77\u6765\u66FE\u56FD\u85E9\u5F88\u5BB9\u6613\u548C\u81EA\u5DF1\u548C\u89E3\u5462\uFF0C\u4E5F\u6709\u53EF\u80FD\u662F\u4ED6\u81EA\u5DF1\u5BF9\u81EA\u5DF1\u8981\u6C42\u5F88\u9AD8\uFF0C\u4E0D\u7BA1\u600E\u4E48\u6837\uFF0C\u6211\u90FD\u505A\u5230\u548C\u81EA\u5DF1\u548C\u89E3\u662F\u6700\u597D\u7684\u4E86\uFF09\u3002</p><p>\u56DE\u5230\u300A\u8096\u7533\u514B\u7684\u6551\u8D4E\u300B\uFF08The Shawshank Redemption\uFF09\uFF0C\u4E3B\u4EBA\u516C\u4E0D\u53EB\u201C\u8096\u7533\u514B\u201D\uFF0C\u4ED6\u53EB Andy Dufresne\uFF08\u5B89\u8FEA\xB7\u675C\u5F17\u4F26\uFF09\u662F\u7531\u8482\u59C6\xB7\u7F57\u5BBE\u65AF\u626E\u6F14\u7684\u3002\u5B89\u8FEA\u662F\u4E2A\u4E8B\u4E1A\u6709\u6210\u7684\u5927\u94F6\u884C\u526F\u603B\u88C1\uFF0C\u81EA\u5DF1\u7684\u59BB\u5B50\u5374\u8DDF\u7740\u522B\u7684\u7537\u4EBA\u8DD1\u4E86\uFF0C\u56E0\u4E3A\u4ED6\u7684\u59BB\u5B50\u89C9\u5F97\u5B89\u8FEA\u5C31\u50CF\u662F\u4E00\u672C\u675F\u4E4B\u9AD8\u9601\u7684\u4E00\u672C\u4E66\uFF0C\u8BA9\u4EBA\u96BE\u4EE5\u7406\u89E3\uFF0C\u4F46\u662F\u5B89\u8FEA\u5341\u5206\u7231\u5979\uFF0C\u53EA\u662F\u5B89\u8FEA\u4E0D\u4F1A\u8868\u8FBE\u3002\u4E4B\u540E\u5B89\u8FEA\u7684\u59BB\u5B50\u548C\u5979\u7684\u60C5\u592B\u88AB\u4EBA\u5728\u665A\u4E0A\u5C04\u6740\uFF0C\u4E00\u5207\u79CD\u79CD\u8FF9\u8C61\u90FD\u5C06\u51F6\u624B\u6307\u5411\u5B89\u8FEA\u3002\u6700\u7EC8\u5B89\u8FEA\u88AB\u5224\u4E24\u4E2A\u7EC8\u8EAB\u76D1\u7981\u3002\u8FD9\u610F\u5473\u7740\uFF0C\u5B89\u8FEA\u5C06\u5728\u6B64\u523B\u5F00\u59CB\uFF0C\u5269\u4E0B\u7684\u4F59\u751F\u90FD\u8981\u5728\u76D1\u72F1\u7684\u9AD8\u5899\u5185\u5EA6\u8FC7\u3002</p><p>\u6211\u5982\u679C\u50CF\u4E4B\u4E0A\u8FD9\u6837\u6765\u53D9\u8FF0\u7535\u5F71\uFF0C\u662F\u5F88\u82B1\u65F6\u95F4\u7684\uFF0C\u6211\u8981\u56DE\u5FC6\u5F71\u7247\uFF0C\u5982\u679C\u518D\u52A0\u4E0A\u81EA\u5DF1\u7684\u89E3\u8BFB\u3002\u6216\u8005\u66F4\u8FD1\u4E00\u6B65\u3002\u5148\u53BB\u770B\u770B\u522B\u4EBA\u7684\u89E3\u8BFB\u3002\u8FD9\u6837\u6765\u7406\u89E3\u5F71\u7247\uFF0C\u50CF\u662F\u4E00\u6B21\u5C31\u8981\u5C06\u7518\u8517\u7684\u7CD6\u6C34\u69A8\u5E72\u4E00\u6837\u3002\u9700\u8981\u82B1\u8D39\u5927\u91CF\u7684\u65F6\u95F4\u548C\u7CBE\u529B\u3002\u6211\u770B\u8FD9\u90E8\u5F71\u7247\u5DF2\u7ECF\u597D\u51E0\u6B21\u4E86\u3002\u4E5F\u5FD8\u8BB0\u7B2C\u4E00\u6B21\u770B\u7684\u611F\u53D7\u4E86\u3002\u66F4\u6CA1\u6709\u4E4B\u524D\u7684\u89C2\u540E\u611F\u53EF\u4EE5\u53C2\u8003\u3002\u4EBA\u751F\u5728\u4E0D\u540C\u7684\u9636\u6BB5\u4F1A\u6709\u4E0D\u540C\u7684\u5883\u9047\uFF0C\u4E5F\u5C31\u6709\u5BF9\u4E8B\u7269\u7684\u4E0D\u540C\u89D2\u5EA6\u7684\u89E3\u8BFB\uFF0C\u83AB\u8BF4\u6211\u8FD9\u6837\u770B\u4E00\u90E8\u7535\u5F71\u4F1A\u5F88\u7D2F\uFF0C\u56E0\u4E3A\u8FD8\u8981\u53BB\u5199\u957F\u957F\u7684\u89C2\u540E\u611F\u3002\u5230\u5982\u4ECA\u6211\u4E5F\u4E0D\u77E5\u9053\u90A3\u79CD\u65B9\u5F0F\u662F\u5BF9\u7684\u3002\u6709\u4EBA\u8BF4\uFF0C\u73B0\u5728\u7684\u4F60\u662F\u4F60\u8FC7\u53BB\u6240\u6709\u7684\u603B\u548C\u3002\u6211\u8BA4\u540C\u8FD9\u53E5\u8BDD\u3002\u56E0\u4E3A\u65F6\u95F4\u4E0D\u80FD\u524D\u540E\u5EF6\u4F38\u548C\u6211\u4EEC\u62E5\u6709\u8BB0\u5FC6\u7684\u5173\u7CFB\u3002\u6211\u4EEC\u88AB\u6628\u5929\u5F71\u54CD\u7740\u3002\u8FC7\u53BB\u7684\u4E8B\u7269\u6F5C\u79FB\u9ED8\u5316\u7684\u5F71\u54CD\u7740\u6211\u4EEC\u3002\u82B1\u8D39\u5927\u91CF\u65F6\u95F4\u53BB\u89E3\u8BFB\uFF0C\u5C31\u4F1A\u52A0\u5F3A\u81EA\u5DF1\u7684\u8FD9\u79CD\u6F5C\u79FB\u9ED8\u5316\u7684\u5F71\u54CD\u529B\u3002</p><p>\u4E0B\u9762\u5C06\u63A5\u7740\u6765\u53D9\u8FF0\u3002\u5B89\u8FEA\u521D\u5230\u8096\u7533\u514B\uFF0C\u603B\u4F1A\u6709\u4E00\u4E9B\u4EBA\u627E\u4ED6\u7684\u9EBB\u70E6\uFF0C\u90A3\u4E9B\u9E21\u5978\u4EEC\uFF0C\u65F6\u5E38\u5BF9\u5B89\u8FEA\u4E0B\u624B\uFF0C\u5B89\u8FEA\u6BCF\u6B21\u90FD\u987D\u5F3A\u53CD\u6297\u3002\u5982\u679C\u6709\u4E00\u6B21\u8BA9\u4ED6\u4EEC\u8F7B\u6613\u5F97\u901E\uFF0C\u90A3\u5BF9\u4E8E\u5B89\u8FEA\u6765\u8BF4\uFF0C\u4EE5\u540E\u7684\u65E5\u5B50\u5C06\u4F1A\u66F4\u96BE\u8FC7\u3002</p><p>\u5B89\u8FEA\u5411\u670D\u5211\u4E8C\u5341\u5E74\u7684\u745E\u5FB7\u6C42\u8D2D\u4E00\u4E2A\u77F3\u9524\uFF0C\u56E0\u4E3A\u5728\u76D1\u72F1\u91CC\u7684\u6F2B\u957F\u751F\u6D3B\uFF0C\u4ED6\u60F3\u91CD\u62FE\u81EA\u5DF1\u8FC7\u53BB\u7684\u7231\u597D\uFF0C\u96D5\u523B\u77F3\u5934\u3002</p><p>\u4E00\u6B21\u6237\u5916\u65BD\u5DE5\uFF0C\u5B89\u8FEA\u5C06\u81EA\u5DF1\u6709\u7406\u8D22\u548C\u62A5\u7A0E\u65B9\u9762\u7684\u624D\u80FD\u5C55\u793A\u7ED9\u4E86\u72F1\u8B66\u4EEC\u3002\u56E0\u6B64\u8FD9\u5E2E\u56DA\u5F92\u53EF\u4EE5\u5728\u521D\u590F\u7684\u65E9\u4E0A\uFF0C\u5750\u5728\u5C4B\u9876\u4E0A\uFF0C\u5439\u7740\u548C\u60A6\u7684\u5FAE\u98CE\uFF0C\u559D\u7740\u72F1\u8B66\u4E70\u7684\u5564\u9152\u3002\u4EAB\u53D7\u8FD9\u975E\u81EA\u7531\u4EBA\u7684\u72EC\u7279\u81EA\u7531\u65F6\u523B\u3002\u8FD9\u5E2E\u72F1\u8B66\u662F\u6CA1\u6709\u529E\u6CD5\u4F53\u4F1A\u5230\u90A3\u79CD\u81EA\u7531\u7684\uFF0C\u56E0\u4E3A\u5C31\u50CF\u662F\uFF0C\u4ED6\u4EEC\u6CA1\u529E\u6CD5\u53BB\u4F53\u4F1A\u4E09\u5929\u5149\u660E\u80FD\u7ED9\u6D77\u4F26\xB7\u51EF\u52D2\u5E26\u6765\u4EC0\u4E48\u6837\u7684\u4F53\u4F1A\u4E00\u6837\u3002</p><p>\u4E4B\u540E\u56E0\u4E3A\u53EF\u4EE5\u9003\u7A0E\u7684\u5173\u7CFB\uFF0C\u5B89\u8FEA\u5728\u76D1\u72F1\u91CC\u6210\u4E86\u7EA2\u4EBA\uFF0C\u6240\u6709\u76D1\u72F1\u7684\u8B66\u5BDF\u90FD\u627E\u4ED6\u5E2E\u5FD9\u3002\u5373\u4F7F\u662F\u5178\u72F1\u957F\u7684\u6BCF\u4E00\u7B14\u9ED1\u8D26\uFF0C\u90FD\u88AB\u5B89\u8FEA\u8BB0\u5F97\u6E05\u6E05\u695A\u695A\u3002\u4E4B\u540E\u5B89\u8FEA\u575A\u6301\u6BCF\u5468\u90FD\u7ED9\u5DDE\u653F\u5E9C\u5199\u4FE1\uFF0C\u8981\u6C42\u8D44\u52A9\u5EFA\u7ACB\u56FE\u4E66\u9986\u3002\u53EA\u5199\u4E86\u516D\u5E74\uFF0C\u5C31\u53D6\u5F97\u4E86\u80DC\u5229\uFF0C\u6709\u8C01\u80FD\u575A\u6301\u516D\u5E74\u3002\u4E4B\u540E\u4E0D\u662F\u505C\u6B62\u5199\u4FE1\uFF0C\u800C\u662F\u6539\u6210\u4E86\u6BCF\u5468\u4E24\u5C01\uFF0C\u4F69\u670D\u3002</p><p>\u5B89\u8FEA\u5728\u65E7\u4E66\u7269\u4E2D\u53D1\u73B0\u4E86\u4E00\u7AE0\u9ED1\u80F6\u5531\u7247\uFF0C\u4ED6\u653E\u97F3\u4E50\u7ED9\u4ED6\u7684\u540C\u4E8B\u4EEC\u542C\u3002\u8BA9\u4ED6\u4EEC\u611F\u53D7\u611F\u53D7\u7247\u523B\u7684\u81EA\u7531\u3002\u7ED3\u679C\u81EA\u5DF1\u906D\u5230\u5355\u72EC\u7D27\u95ED\u4E00\u4E2A\u6708\u3002</p><p>\u4E3A\u4E86\u7247\u523B\u7684\u81EA\u7531\uFF0C\u4ED8\u51FA\u7684\u662F\u4E24\u5468\u7684\u8270\u96BE\u65F6\u5149\u3002\u90A3\u771F\u662F\u65E0\u6BD4\u7684\u8D35\u91CD\u3002\u6B63\u662F\u56E0\u4E3A\u81EA\u7531\u7684\u8D35\u91CD\uFF0C\u624D\u8BA9\u5B89\u8FEA\u73CD\u60DC\u548C\u6E34\u671B\u3002</p><p>\u4E24\u5468\u7684\u5355\u72EC\u5173\u62BC\u65F6\u5149\u5BF9\u4E8E\u522B\u4EBA\u6765\u8BF4\u53EF\u80FD\u662F\u5EA6\u65E5\u5982\u5E74\uFF0C\u4F46\u662F\u5BF9\u4E8E\u5B89\u8FEA\uFF0C\u97F3\u4E50\u4E4B\u7F8E\u5728\u4ED6\u5FC3\u91CC\uFF0C\u5728\u4ED6\u8111\u5B50\u91CC\uFF0C\u5916\u9762\u7684\u9ED1\u6697\u662F\u4E0D\u80FD\u628A\u5B83\u4EEC\u593A\u8D70\u7684\u3002</p><p>\u4EC0\u4E48\u662F\u6211\u6700\u6E34\u671B\u7684\u81EA\u7531\u5462\uFF0C\u90A3\u79CD\u65E0\u6BD4\u73CD\u8D35\u7684\u4E1C\u897F\uFF0C\u503C\u5F97\u81EA\u5DF1\u4ED8\u51FA\u4E24\u5468\u7684\u8270\u96BE\u65F6\u5149\u53BB\u6362\u5417\uFF1F</p><p>\u5B89\u8FEA\u5C06\u4E4B\u540E\u7684\u76D1\u72F1\u751F\u6D3B\u5E0C\u671B\u5BC4\u6258\u5728\u4E86\u4ED6\u7684\u5B66\u751F\uFF0C\u5C0F\u5E9E\u514B\u8EAB\u4E0A\uFF0C\u5C0F\u5E9E\u514B\u60F3\u5B66\u4E60\u77E5\u8BC6\uFF0C\u8003\u53D6\u5B66\u5386\uFF0C\u6700\u540E\u4ED6\u6210\u529F\u4E86\u3002\u4F46\u662F\u4ED6\u544A\u8BC9\u4E86\u5B89\u8FEA\u81EA\u5DF1\u53EF\u4EE5\u8BC1\u660E\u5B89\u8FEA\u662F\u88AB\u51A4\u6789\u8FDB\u5165\u76D1\u72F1\u7684\u3002\u7ED3\u679C\u5F88\u5FEB\uFF0C\u5C0F\u5E9E\u514B\u5C31\u88AB\u5178\u72F1\u957F\u6740\u5BB3\u4E86\u3002\u5B89\u8FEA\u83B7\u5F97\u81EA\u7531\u7684\u6700\u540E\u673A\u4F1A\u5C31\u8FD9\u6837\u8F7B\u6613\u7684\u88AB\u5178\u72F1\u957F\u62B9\u6389\u4E86\u3002</p><p>\u5B89\u8FEA\u7279\u522B\u7EDD\u671B\uFF0C\u5F53\u5E0C\u671B\u51FA\u73B0\u7684\u65F6\u5019\uFF0C\u7A81\u7136\u51FA\u73B0\u7684\u66D9\u5149\u518D\u6B21\u88AB\u9ED1\u6697\u906E\u853D\uFF0C\u90A3\u79CD\u6ECB\u5473\u8FD8\u4E0D\u5982\u4E0D\u8981\u51FA\u73B0\u5E0C\u671B\u7684\u597D\u3002\u4E0D\u77E5\u9053\u5B89\u8FEA\u662F\u4E0D\u662F\u8FD9\u4E48\u8BA4\u4E3A\u7684\u3002\u56E0\u4E3A\u6211\u6CA1\u6709\u5B89\u8FEA\u7684\u6BC5\u529B\u3002\u548C\u6562\u4E8E\u63A5\u53D7\u5931\u8D25\u6253\u51FB\u7684\u52C7\u6C14\u3002</p><p>\u5DF2\u7ECF\u5341\u4E5D\u5E74\u4E86\uFF0C\u4E00\u4E2A\u4EBA\u7684\u5927\u90E8\u5206\u751F\u547D\u90FD\u5728\u8FD9\u94DC\u5899\u94C1\u58C1\u91CC\u5EA6\u8FC7\u4E86\u3002\u5728\u8FD9\u91CC\uFF0C\u4F60\u62E5\u6709\u6700\u591A\u7684\u5C31\u662F\u65F6\u95F4\uFF0C\u65F6\u95F4\u4F1A\u6162\u5F97\u5C31\u50CF\u6D41\u6C34\u4FB5\u8680\u5CA9\u77F3\u4E00\u822C\u3002\u6BCF\u4E2A\u4EBA\u90FD\u9700\u8981\u6709\u4E2A\u81EA\u5DF1\u7684\u7231\u597D\uFF0C\u624D\u80FD\u6253\u53D1\u8FD9\u65E0\u804A\u7684\u65F6\u5149\u3002\u60F3\u60F3\u4E3A\u4EC0\u4E48\u72AF\u7F6A\u4F1A\u88AB\u5224\u82E5\u5E72\u5E74\u7684\u76D1\u7981\u3002\u65F6\u95F4\u548C\u81EA\u7531\u5BF9\u4E8E\u4E00\u4E2A\u4EBA\u6765\u8BF4\uFF0C\u662F\u592A\u5B9D\u8D35\u4E86\u3002\u8981\u4E0D\u7136\u600E\u4E48\u4F1A\u4EE5\u65F6\u95F4\u6765\u8861\u91CF\u4E00\u4E2A\u4EBA\u7684\u72AF\u7F6A\u7A0B\u5EA6\u3002\u5341\u4E5D\u5E74\u91CC\uFF0C\u5B89\u8FEA\u7528\u77F3\u9524\u51FF\u51FA\u4E86\u4E00\u6761\u5730\u9053\uFF0C\u901A\u5F80\u81EA\u7531\u7684\u5730\u9053\uFF0C\u6BCF\u4E2A\u6DF1\u591C\u90FD\u4F1A\u5728\u5B64\u72EC\u4E2D\u884C\u8FDB\uFF0C\u5C31\u8FD9\u6837\u4E00\u5BF8\u4E00\u5BF8\u7684\u53BB\u51FF\uFF0C\u6CA1\u6709\u653E\u5F03\u65F6\u5019\uFF0C\u6CA1\u6709\u4EC0\u4E48\u522B\u7684\u4E1C\u897F\u53EF\u4EE5\u66FF\u4EE3\u8FD9\u4EF6\u4E8B\u3002\u53EA\u662F\u4E3A\u4E86\u81EA\u7531\uFF0C\u4E3A\u4E86\u5916\u9762\u7684\u65B0\u9C9C\u7A7A\u6C14\u3002\u4ED8\u51FA\u7684\u6240\u6709\u90FD\u662F\u503C\u5F97\u7684\u3002</p><p>\u5B89\u8FEA\u662F\u4F1F\u5927\u7684\u3002</p><p>\u6211\u5C31\u60F3\u662F\u88AB\u5173\u8FDB\u76D1\u72F1\u4E00\u6837\uFF0C\u6211\u8FD8\u6CA1\u6709\u5F00\u59CB\u51FF\u90A3\u901A\u5F80\u81EA\u7531\u4E4B\u8DEF\u7684\u5730\u9053\u3002\u4F46\u662F\u6211\u77E5\u9053\u4E86\u81EA\u5DF1\uFF0C\u5C31\u50CF\u662F\u76D1\u72F1\u91CC\u7684\u5B89\u8FEA\u4E00\u6837\uFF0C\u6211\u62E5\u6709\u6700\u591A\u7684\u5C31\u662F\u4E8B\u4EF6\u3002\u53EF\u662F\u6211\u7F3A\u5C11\u5E0C\u671B\u3002</p><p>hope is a good thing,maybe the best of things. And no good thing ever dies.\uFF08\u5E0C\u671B\u662F\u4EF6\u597D\u4E8B\uFF0C\u4E5F\u8BB8\u662F\u6700\u597D\u7684\u4E8B\uFF0C\u800C\u7F8E\u597D\u7684\u4E8B\u6C38\u4E0D\u6D88\u901D\u3002\uFF09</p><p>\u6700\u540E\u7684\u7ED3\u5C40\u662F\u7F8E\u597D\u7684\uFF0C\u5B89\u8FEA\u548C\u745E\u5FB7\u5728\u6CA1\u6709\u56DE\u5FC6\u7684\u6C99\u6EE9\u4E0A\u81EA\u7531\u7684\u5EA6\u8FC7\u751F\u547D\u5269\u4E0B\u7684\u65F6\u5149\u3002</p><p>\u81EA\u5DF1\u7684\u89E3\u8BFB\u6CA1\u6709\u5199\u591A\u5C11\uFF0C\u8FD9\u6837\u4E5F\u633A\u597D\u7684\u3002</p>',53);function t(r,h){return o}var s=p(a,[["render",t],["__file","2020-06-19.html.vue"]]);export{s as default};
