import{_ as p,c as r}from"./app.36b123b1.js";const t={},e=r('<h1 id="\u6211\u7684\u7236\u4EB2\u6BCD\u4EB2" tabindex="-1"><a class="header-anchor" href="#\u6211\u7684\u7236\u4EB2\u6BCD\u4EB2" aria-hidden="true">#</a> \u6211\u7684\u7236\u4EB2\u6BCD\u4EB2</h1><p>\u6587/\u4EFB\u6B63\u975E</p><p>\u3010\u4E00\u3011</p><p>\u4E0A\u4E16\u7EAA\u672B\u6700\u540E\u4E00\u5929\uFF0C\u6211\u603B\u7B97\u826F\u5FC3\u53D1\u73B0\uFF0C\u5728\u516C\u52A1\u7ED3\u675F\u4E4B\u540E\uFF0C\u4E70\u4E86\u4E00\u5F20\u4ECE\u5317\u4EAC\u53BB\u6606\u660E\u7684\u673A\u7968\uFF0C\u53BB\u770B\u770B\u5988\u5988\u3002</p><p>\u4E70\u597D\u673A\u7968\u540E\uFF0C\u6211\u6CA1\u6709\u7ED9\u5979\u6253\u7535\u8BDD\uFF0C\u6211\u77E5\u9053\u4E00\u6253\u7535\u8BDD\u5979\u4E00\u4E0B\u5348\u90FD\u4F1A\u5FD9\u788C\uFF0C\u4E0D\u7BA1\u591A\u665A\u5230\u8FBE\uFF0C\u90FD\u4F1A\u7ED9\u6211\u505A\u4E00\u4E9B\u6211\u5C0F\u65F6\u5019\u559C\u6B22\u5403\u7684\u4E1C\u897F\u3002\u76F4\u5230\u98DE\u673A\u8D77\u98DE\uFF0C\u6211\u624D\u544A\u8BC9\u5979\uFF0C\u8BA9\u5979\u4E0D\u8981\u544A\u8BC9\u522B\u4EBA\uFF0C\u4E0D\u8981\u8F66\u6765\u63A5\uFF0C\u6211\u81EA\u5DF1\u5750\u51FA\u79DF\u8F66\u56DE\u5BB6\uFF0C\u76EE\u7684\u5C31\u662F\u597D\u597D\u966A\u966A\u5979\u3002</p><p>\u524D\u51E0\u5E74\u6211\u6BCF\u5E74\u4E5F\u53BB\u770B\u770B\u5988\u5988\uFF0C\u4F46\u4E00\u4E0B\u98DE\u673A\u5C31\u7ED9\u529E\u4E8B\u5904\u63A5\u8D70\u4E86\uFF0C\u8BF4\u8FD9\u4E2A\u5BA2\u6237\u5F88\u91CD\u8981\uFF0C\u8981\u62DC\u89C1\u4E00\u4E0B\uFF0C\u90A3\u4E2A\u5BA2\u6237\u5F88\u91CD\u8981\uFF0C\u8981\u966A\u4ED6\u4EEC\u5403\u987F\u996D\uFF0C\u5FD9\u6765\u5FD9\u53BB\uFF0C\u5FD9\u5230\u4E0A\u98DE\u673A\u65F6\u56DE\u5BB6\u53D6\u884C\u674E\uFF0C\u4E0E\u7236\u6BCD\u5306\u5306\u544A\u522B\u3002\u5988\u5988\u76FC\u661F\u661F\u3001\u76FC\u6708\u4EAE\uFF0C\u76FC\u5520\u5520\u5BB6\u5E38\uFF0C\u5374\u4E00\u6B21\u53C8\u4E00\u6B21\u5730\u843D\u7A7A\u3002</p><p><strong>\u3010\u4E8C\u3011</strong></p><p>\u4E00\u4E2A\u666E\u901A\u7684\u65E9\u4E0A\uFF0C\u5988\u5988\u4ECE\u83DC\u5E02\u573A\u51FA\u6765\uFF0C\u63D0\u7740\u4E24\u5C0F\u5305\u83DC\uFF0C\u88AB\u6C7D\u8F66\u649E\u6210\u91CD\u4F24\u3002\u6211\u8EAB\u5728\u4F0A\u6717\uFF0C\u98DE\u673A\u8981\u591A\u6B21\u4E2D\u8F6C\u624D\u80FD\u56DE\u6765\uFF0C\u5728\u5DF4\u6797\u8F6C\u673A\u8981\u5F856.5\u4E2A\u5C0F\u65F6\uFF0C\u771F\u662F\u5FC3\u5982\u714E\u71AC\uFF0C\u53C8\u9047\u5DF4\u6797\u96F7\u96E8\uFF0C\u98DE\u673A\u5EF6\u8BEF\u4E24\u4E2A\u5C0F\u65F6\uFF0C\u5230\u66FC\u8C37\u65F6\u53C8\u665A\u4E8610\u5206\u949F\uFF0C\u6CA1\u6709\u53CA\u65F6\u8D76\u4E0A\u56DE\u6606\u660E\u7684\u98DE\u673A\uFF0C\u76F4\u5230\u6DF1\u591C\u624D\u8D76\u5230\u6606\u660E\u3002</p><p>\u56DE\u5230\u6606\u660E\uFF0C\u5C31\u77E5\u9053\u5988\u5988\u4E0D\u884C\u4E86\uFF0C\u5979\u7684\u5934\u90E8\u5168\u90E8\u7ED9\u649E\u574F\u4E86\uFF0C\u5F53\u65F6\u7684\u5FC3\u8DF3\u3001\u547C\u5438\u5168\u662F\u9760\u836F\u7269\u548C\u673A\u5668\u7EF4\u6301\uFF0C\u4E4B\u6240\u4EE5\u5728\u7535\u8BDD\u4E0A\u4E0D\u544A\u8BC9\u6211\uFF0C\u662F\u6015\u6211\u5728\u65C5\u9014\u4E2D\u51FA\u4E8B\u3002\u6211\u770B\u89C1\u5988\u5988\u4E00\u58F0\u4E0D\u54CD\u5730\u5B89\u8BE6\u5730\u8EBA\u5728\u75C5\u5E8A\u4E0A\uFF0C\u4E0D\u7528\u64CD\u52B3\u3001\u70E6\u5FC3\uFF0C\u597D\u50CF\u5979\u4E00\u751F\u4E5F\u6CA1\u6709\u8FD9\u4E48\u4F11\u606F\u8FC7\u3002</p><p>\u6211\u771F\u540E\u6094\u6CA1\u6709\u5728\u4F0A\u6717\u7ED9\u5988\u5988\u6253\u4E00\u4E2A\u7535\u8BDD\u3002\u56E0\u4E3A\u4EE5\u524D\u4E0D\u7BA1\u6211\u5728\u56FD\u5185\u3001\u56FD\u5916\u7ED9\u5979\u6253\u7535\u8BDD\u65F6\uFF0C\u5979\u90FD\u5520\u53E8\uFF1A\u201C\u4F60\u53C8\u51FA\u5DEE\u4E86\u201D\uFF0C\u201C\u975E\u975E\u4F60\u7684\u8EAB\u4F53\u8FD8\u4E0D\u5982\u6211\u597D\u5462\u201D\uFF0C\u201C\u975E\u975E\u4F60\u7684\u76B1\u7EB9\u6BD4\u5988\u5988\u8FD8\u591A\u5462\u201D\uFF0C\u201C\u975E\u975E\u4F60\u8D70\u8DEF\u8FD8\u4E0D\u5982\u6211\u5462\uFF0C\u4F60\u8FD9\u4E48\u5E74\u7EAA\u8F7B\u8F7B\u5C31\u8FD9\u4E48\u591A\u75C5\u201D\uFF0C\u201C\u975E\u975E\uFF0C\u7CD6\u5C3F\u75C5\u53C2\u52A0\u5BB4\u4F1A\u591A\u4E86\uFF0C\u574F\u5F97\u66F4\u5FEB\u5462\uFF0C\u4F60\u7684\u5FC3\u810F\u53C8\u4E0D\u597D\u201D\u2026\u2026</p><p>\u6211\u60F3\u4F0A\u6717\u6761\u4EF6\u8FD9\u4E48\u5DEE\uFF0C\u6211\u4E00\u6253\u7535\u8BDD\uFF0C\u5988\u5988\u53C8\u5520\u53E8\uFF0C\u53CD\u6B63\u8FC7\u4E0D\u4E86\u51E0\u5929\u5C31\u89C1\u9762\u4E86\uFF0C\u5C31\u6CA1\u6709\u6253\uFF0C\u800C\u8FD9\u662F\u6211\u4E00\u751F\u4E2D\u6700\u5927\u7684\u61BE\u4E8B\u3002\u5982\u679C\u6211\u771F\u6253\u4E86\uFF0C\u62D6\u5EF6\u5979\u4E00\u4E24\u5206\u949F\u51FA\u95E8\uFF0C\u4E5F\u8BB8\u5988\u5988\u5C31\u8EB2\u8FC7\u4E86\u8FD9\u573A\u707E\u96BE\u3002\u8FD9\u79CD\u6094\u6068\u7684\u5FC3\u60C5\uFF0C\u771F\u662F\u96BE\u4EE5\u5F62\u5BB9\u3002</p><p>\u6211\u770B\u4E86\u5988\u5988\u6700\u540E\u4E00\u773C\uFF0C\u5988\u5988\u6E98\u7136\u53BB\u4E16\u3002</p><p><strong>\u3010\u4E09\u3011</strong></p><p>1995\u5E74\uFF0C\u6211\u7236\u4EB2\u5728\u6606\u660E\u8857\u5934\u7684\u5C0F\u644A\u4E0A\u4E70\u4E86\u4E00\u74F6\u5851\u6599\u5305\u88C5\u7684\u8F6F\u996E\u6599\uFF0C\u559D\u540E\u62C9\u809A\u5B50\uFF0C\u4E00\u76F4\u5230\u5168\u8EAB\u8870\u7AED\u53BB\u4E16\u3002</p><p>\u7236\u4EB2\u4EFB\u6469\u900A\uFF0C\u5C3D\u804C\u5C3D\u8D23\u4E00\u751F\uFF0C\u53EF\u4EE5\u8BF4\u662F\u4E00\u4E2A\u4E61\u6751\u6559\u80B2\u5BB6\u3002\u5988\u5988\u7A0B\u8FDC\u662D\uFF0C\u662F\u4E00\u4E2A\u966A\u4F34\u7236\u4EB2\u5728\u8D2B\u56F0\u5C71\u533A\u4E0E\u7A77\u5B69\u5B50\u53AE\u6DF7\u4E86\u4E00\u751F\u7684\u4E00\u4E2A\u666E\u901A\u5F97\u4E0D\u80FD\u518D\u666E\u901A\u7684\u56ED\u4E01\u3002</p><p>\u7236\u4EB2\u7A7F\u7740\u571F\u6539\u5DE5\u4F5C\u961F\u7684\u68C9\u8863\uFF0C\u968F\u89E3\u653E\u519B\u527F\u532A\u90E8\u961F\u4E00\u540C\u8FDB\u5165\u8D35\u5DDE\u5C11\u6570\u6C11\u65CF\u5C71\u533A\u53BB\u7B79\u5EFA\u4E00\u6240\u6C11\u65CF\u4E2D\u5B66\u3002\u4E00\u5934\u624E\u8FDB\u53BB\u5C31\u662F\u51E0\u5341\u5E74\uFF0C\u4ED6\u57F9\u517B\u7684\u5B66\u751F\u4E0D\u5C11\u6210\u4E3A\u515A\u548C\u56FD\u5BB6\u7684\u9AD8\u7EA7\u5E72\u90E8\uFF0C\u6709\u4E9B\u8FD8\u662F\u4E2D\u592E\u9662\u6821\u7684\u6821\u7EA7\u9886\u5BFC\uFF0C\u800C\u7236\u4EB2\u8FD8\u662F\u90A3\u4E48\u4F4D\u5351\u8A00\u5FAE\u3002</p><p><strong>\u3010\u56DB\u3011</strong></p><p>\u6211\u4E0E\u7236\u6BCD\u76F8\u5904\u7684\u9752\u5C11\u5E74\u65F6\u4EE3\uFF0C\u5370\u8C61\u6700\u6DF1\u7684\u5C31\u662F\u5EA6\u8FC7\u4E09\u5E74\u81EA\u7136\u707E\u5BB3\u7684\u56F0\u96BE\u65F6\u671F\u3002\u4ECA\u5929\u60F3\u6765\u8FD8\u5386\u5386\u5728\u76EE\u3002</p><p>\u6211\u4EEC\u5144\u59B9\u4E03\u4E2A\uFF0C\u52A0\u4E0A\u7236\u6BCD\u5171\u4E5D\u4EBA\u3002\u5168\u9760\u7236\u6BCD\u5FAE\u8584\u7684\u5DE5\u8D44\u6765\u751F\u6D3B\uFF0C\u6BEB\u65E0\u5176\u4ED6\u6765\u6E90\u3002\u672C\u6765\u751F\u6D3B\u5C31\u5341\u5206\u56F0\u96BE\uFF0C\u513F\u5973\u4E00\u5929\u5929\u5728\u957F\u5927\uFF0C\u8863\u670D\u4E00\u5929\u5929\u5728\u53D8\u77ED\uFF0C\u800C\u4E14\u90FD\u8981\u8BFB\u4E66\uFF0C\u5F00\u652F\u5F88\u5927\uFF0C\u6BCF\u4E2A\u5B66\u671F\u6BCF\u4EBA\u8981\u4EA42-3\u5143\u7684\u5B66\u8D39\uFF0C\u5230\u4EA4\u8D39\u65F6\uFF0C\u5988\u5988\u6BCF\u6B21\u90FD\u53D1\u6101\u3002\u6211\u7ECF\u5E38\u770B\u5230\u5988\u5988\u6708\u5E95\u5C31\u5230\u5904\u5411\u4EBA\u501F\u94B1\u5EA6\u9965\u8352\uFF0C\u800C\u4E14\u5E38\u5E38\u8D70\u4E86\u51E0\u5BB6\u90FD\u672A\u5FC5\u501F\u5230\u3002</p><p>\u76F4\u5230\u9AD8\u4E2D\u6BD5\u4E1A\u6211\u6CA1\u6709\u7A7F\u8FC7\u886C\u8863\u3002\u6709\u540C\u5B66\u770B\u5230\u5F88\u70ED\u7684\u5929\uFF0C\u6211\u8FD8\u7A7F\u7740\u539A\u539A\u7684\u5916\u8863\uFF0C\u5C31\u8BA9\u6211\u5411\u5988\u5988\u8981\u4E00\u4EF6\u886C\u8863\uFF0C\u6211\u4E0D\u6562\uFF0C\u56E0\u4E3A\u6211\u77E5\u9053\u505A\u4E0D\u5230\u3002\u6211\u4E0A\u5927\u5B66\u65F6\u5988\u5988\u4E00\u6B21\u9001\u6211\u4E24\u4EF6\u886C\u8863\uFF0C\u6211\u771F\u60F3\u54ED\uFF0C\u56E0\u4E3A\uFF0C\u6211\u6709\u886C\u8863\u4E86\uFF0C\u5F1F\u59B9\u4EEC\u5C31\u4F1A\u66F4\u96BE\u4E86\u3002\u6211\u5BB6\u5F53\u65F6\u662F2-3\u4EBA\u5408\u7528\u4E00\u6761\u88AB\u76D6\uFF0C\u800C\u4E14\u7834\u65E7\u7684\u88AB\u5355\u4E0B\u9762\u94FA\u7684\u662F\u7A3B\u8349\u3002</p><p>\u4E0A\u5927\u5B66\u6211\u8981\u62FF\u8D70\u4E00\u6761\u88AB\u5B50\uFF0C\u5C31\u66F4\u56F0\u96BE\u4E86\uFF0C\u56E0\u4E3A\u90A3\u65F6\u8FD8\u5B9E\u884C\u5E03\u7968\u3001\u68C9\u82B1\u7968\u7BA1\u5236\uFF0C\u6700\u5C11\u7684\u4E00\u5E74\uFF0C\u6BCF\u4EBA\u53EA\u53D10.5\u7C73\u5E03\u7968\u3002\u6CA1\u6709\u88AB\u5355\uFF0C\u5988\u5988\u6361\u4E86\u6BD5\u4E1A\u5B66\u751F\u4E22\u5F03\u7684\u51E0\u5E8A\u7834\u88AB\u5355\u7F1D\u7F1D\u8865\u8865\uFF0C\u6D17\u5E72\u51C0\uFF0C\u8FD9\u6761\u88AB\u5355\u5C31\u5728\u91CD\u5E86\u966A\u6211\u5EA6\u8FC7\u4E86\u4E94\u5E74\u7684\u5927\u5B66\u751F\u6D3B\u3002</p><p>\u6211\u4EEC\u5BB6\u5F53\u65F6\u6BCF\u9910\u5B9E\u884C\u4E25\u683C\u5206\u996D\u5236\uFF0C\u63A7\u5236\u6240\u6709\u4EBA\u6B32\u671B\u7684\u914D\u7ED9\u5236\uFF0C\u4FDD\u8BC1\u4EBA\u4EBA\u90FD\u80FD\u6D3B\u4E0B\u6765\u3002\u5982\u679C\u4E0D\u662F\u8FD9\u6837\uFF0C\u603B\u4F1A\u6709\u4E00\u4E2A\u3001\u4E24\u4E2A\u5F1F\u59B9\u6D3B\u4E0D\u5230\u4ECA\u5929\u3002\u6211\u771F\u6B63\u80FD\u7406\u89E3\u6D3B\u4E0B\u53BB\u8FD9\u53E5\u8BDD\u7684\u542B\u4E49\u3002</p><p>\u9AD8\u4E09\u5FEB\u9AD8\u8003\u65F6\uFF0C\u6211\u6709\u65F6\u5728\u5BB6\u590D\u4E60\u529F\u8BFE\uFF0C\u5B9E\u5728\u997F\u5F97\u53D7\u4E0D\u4E86\u4E86\uFF0C\u7528\u7C73\u7CE0\u548C\u83DC\u5408\u4E00\u4E0B\uFF0C\u70D9\u7740\u5403\uFF0C\u88AB\u7236\u4EB2\u78B0\u4E0A\u51E0\u6B21\uFF0C\u4ED6\u5FC3\u75BC\u4E86\u3002\u5176\u5B9E\u90A3\u65F6\u6211\u5BB6\u7A77\u5F97\u8FDE\u4E00\u4E2A\u53EF\u4E0A\u9501\u7684\u67DC\u5B50\u90FD\u6CA1\u6709\uFF0C\u7CAE\u98DF\u662F\u7528\u74E6\u7F38\u88C5\u7740\uFF0C\u6211\u4E5F\u4E0D\u6562\u53BB\u968F\u4FBF\u6293\u4E00\u628A\u3002</p><p>\u9AD8\u8003\u524D\u4E09\u4E2A\u6708\uFF0C\u5988\u5988\u7ECF\u5E38\u5728\u65E9\u4E0A\u585E\u7ED9\u6211\u4E00\u4E2A\u5C0F\u5C0F\u7684\u7389\u7C73\u997C\uFF0C\u8981\u6211\u5B89\u5FC3\u590D\u4E60\u529F\u8BFE\uFF0C\u6211\u80FD\u8003\u4E0A\u5927\u5B66\uFF0C\u5C0F\u7389\u7C73\u997C\u529F\u52B3\u5DE8\u5927\u3002\u5982\u679C\u4E0D\u662F\u8FD9\u6837\uFF0C\u4E5F\u8BB8\u6211\u5C31\u8FDB\u4E0D\u4E86\u534E\u4E3A\u8FD9\u6837\u7684\u516C\u53F8\uFF0C\u793E\u4F1A\u4E0A\u591A\u4E86\u4E00\u540D\u517B\u732A\u80FD\u624B\uFF0C\u6216\u8857\u8FB9\u591A\u4E86\u4E00\u540D\u80FD\u5DE5\u5DE7\u5320\u800C\u5DF2\u3002\u8FD9\u4E2A\u5C0F\u5C0F\u7684\u7389\u7C73\u997C\uFF0C\u662F\u4ECE\u7236\u6BCD\u4E0E\u5F1F\u59B9\u7684\u53E3\u4E2D\u62A0\u51FA\u6765\u7684\uFF0C\u6211\u65E0\u4EE5\u62A5\u7B54\u4ED6\u4EEC\u3002</p><p>\u201C\u8BB0\u4F4F\u77E5\u8BC6\u5C31\u662F\u529B\u91CF\uFF0C\u522B\u4EBA\u4E0D\u5B66\uFF0C\u4F60\u8981\u5B66\uFF0C\u4E0D\u8981\u968F\u5927\u6D41\u3002\u201D\u201C\u4EE5\u540E\u6709\u80FD\u529B\u8981\u5E2E\u52A9\u5F1F\u59B9\u3002\u201D\u80CC\u8D1F\u7740\u8FD9\u79CD\u91CD\u6258\uFF0C\u6211\u5728\u5F53\u65F6\u7684\u73AF\u5883\u4E0B\uFF0C\u5C06\u6A0A\u6620\u5DDD\u7684\u9AD8\u7B49\u6570\u5B66\u4E60\u9898\u96C6\u4ECE\u5934\u5230\u5C3E\u505A\u4E86\u4E24\u904D\uFF0C\u5B66\u4E60\u4E86\u903B\u8F91\u3001\u54F2\u5B66\u3002\u8FD8\u81EA\u5B66\u4E86\u4E09\u95E8\u5916\u8BED\uFF0C\u5F53\u65F6\u5DF2\u5230\u53EF\u4EE5\u9605\u8BFB\u5927\u5B66\u8BFE\u672C\u7684\u7A0B\u5EA6\uFF0C\u7EC8\u56E0\u6211\u4E0D\u662F\u8BED\u8A00\u5929\u624D\uFF0C\u52A0\u4E4B\u5728\u519B\u961F\u670D\u52A1\u65F6\u7528\u4E0D\u4E0A\uFF0C20\u591A\u5E74\u8352\u5E9F\uFF0C\u5B8C\u5168\u5FD8\u5149\u4E86\u3002</p><p>\u6211\u5F53\u5E74\u7A7F\u8D70\u7236\u4EB2\u7684\u76AE\u978B\uFF0C\u6CA1\u5FF5\u53CA\u7236\u4EB2\u90A3\u65F6\u662F\u505A\u82E6\u5DE5\u7684\uFF0C\u6CE5\u91CC\u6C34\u91CC\uFF0C\u51B0\u51B7\u6F6E\u6E7F\uFF0C\u4ED6\u66F4\u9700\u8981\u978B\u5B50\u3002\u73B0\u5728\u56DE\u5FC6\u8D77\u6765\uFF0C\u611F\u89C9\u81EA\u5DF1\u592A\u81EA\u79C1\u4E86\u3002</p><p>\u56DE\u987E\u6211\u81EA\u5DF1\u5DF2\u8D70\u8FC7\u7684\u5386\u53F2\uFF0C\u552F\u4E00\u6709\u6127\u7684\u662F\u5BF9\u4E0D\u8D77\u7236\u6BCD\uFF0C\u6CA1\u6761\u4EF6\u65F6\u6CA1\u6709\u7167\u987E\u4ED6\u4EEC\uFF0C\u6709\u6761\u4EF6\u65F6\u4E5F\u6CA1\u6709\u7167\u987E\u4ED6\u4EEC\u3002</p><p>\u7238\u7238\uFF0C\u5988\u5988\uFF0C\u5343\u58F0\u4E07\u58F0\u547C\u5524\u4F60\u4EEC\uFF0C\u5343\u58F0\u4E07\u58F0\u5524\u4E0D\u56DE\u3002</p><p>\u901D\u8005\u5DF2\u7ECF\u901D\u53BB\uFF0C\u6D3B\u7740\u7684\u8FD8\u8981\u524D\u884C\u3002</p><p>\u2014\u20142001\u5E742\u67088\u65E5\u4E8E\u6DF1\u5733</p>',30);function _(a,n){return e}var o=p(t,[["render",_],["__file","\u6211\u7684\u7236\u4EB2\u6BCD\u4EB2.html.vue"]]);export{o as default};
